mapper.select:
select count(*) from member_tb
where member_id = #{member_id}
and member_pw = #{member_pw}

select * from member_tb
where member_id = #{member_id}
and member_pw = #{member_pw}

mapper.insert:
INSERT INTO file_tb
	(
	file_name,
	file_path
	)
	VALUES
	(
	#{file_name},
	#{file_path}
	);

mapper.select:
SELECT * FROM file_tb
WHERE 
	file_name = #{file_name}
AND
	file_path = #{file_path}
limit 1

mapper.delete:
delete FROM member_tb
WHERE member_id = #{member_id}
AND member_pw = #{member_pw}

mapper.update:
update member_tb set member_pw = #{member_pw}, member_nickname = #{member_nickname}, member_name = #{member_name}, member_phone = #{member_phone}, member_email = #{member_email}, member_address = #{member_address},
file_idx = #{file_idx},
member_amnd_date = now() where member_id = #{member_id}
